sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
    if (isLoading || newValue === '') {
        if (g_form.isNewRecord()) {
            var glideAjOne = new GlideAjax('GetEmailAddress');
            glideAjOne.addParam('sysparm_name', 'getEmail');
            glideAjOne.addParam('sysparm_userID', g_form.getValue('u_requested_for'));
            glideAjOne.getXMLAnswer(getTheEmail);
        }
        return;
    }

    //Type appropriate comment here, and begin script below
    var glideAj = new GlideAjax('GetEmailAddress');
    glideAj.addParam('sysparm_name', 'getEmail');
    glideAj.addParam('sysparm_userID', g_form.getValue('u_requested_for'));
    glideAj.getXMLAnswer(getTheEmail);

    function getTheEmail(response) {
        //var newEmail = response.responseXML.documentElement.getAttribute('answer');
        g_form.clearValue('u_requested_for_email');
        g_form.setValue('u_requested_for_email', response);
    }

}
