sys_script_email.script:
(function runMailScript(/* GlideRecord */ current, /* TemplatePrinter */ template,
          /* Optional EmailOutbound */ email, /* Optional GlideRecord */ email_action,
          /* Optional GlideRecord */ event) {

          // Add your code here
	var rightNow = new GlideDateTime();
	var overdueNITask = new GlideRecord('x_58872_needit_needit_task');
	overdueNITask.addQuery('due_date','<=',rightNow);
	overdueNITask.addQuery('state','<',3);
	overdueNITask.addQuery('assigned_to',current.assigned_to);
	overdueNITask.query();
	if((overdueNITask.getRowCount()-1)>1){
	//Following line prints the message to the email
	template.print("In addition to this overdue NeedIt Task, you have "+(overdueNITask.getRowCount()-1)+" more overdue NeedIt Tasks.");
	}
})(current, template, email, email_action, event);
